: right;        });    });</script><script>    $(".menu-toggle").click(function (e) {        e.preventDefault();        $(".wrapper").toggleClass("toggled");    });</script><script>    function ready(){        var a = document.querySelector('#first_menu');        a.onmouseout=function(e){            document.getElementById('second').style.display='none';            var b=document.getElementsByClassName('first');            var elements=b[0].getElementsByTagName('li');            for(var i=0;i<elements.length; i++){                var input=elements[i];                input.style.lineHeight=1.5;            }        };        a.onmouseover=function (e) {            document.getElementById('second').style.display='block';            var b=document.getElementsByClassName('first');            var elements=b[0].getElementsByTagName('li');            for(var i=0;i<elements.length; i++){                var input=elements[i];                input.style.lineHeight=1;            }        };    };    document.addEventListener("DOMContentLoaded", ready);</script></body></html>